import { useState } from "react";

const Button = ({text, onClick}) => <button onClick={onClick}>{text}</button>

const Statistic = ({gut, schlecht, neutral}) => {
  const gesamtZahl = gut+schlecht+neutral
  if(gesamtZahl>0){
    return(
      <div>
        <table>
          <tr>
            <th>gut</th>
            <th>neutral</th>
            <th>schlecht</th>
          </tr>
          <tr>
            <td>{gut}</td>
            <td>{neutral}</td>
            <td>{schlecht}</td>
          </tr>
        </table>
          <p>
          Durchschnitt: {(gut*1 + schlecht*-1)/gesamtZahl}<br />
          Positiv-Quote: {gut/gesamtZahl}
          </p>
      </div>
    )
  }

  return(<div>Sei die erste Person die bewertet!</div>)

}

const App = () => {
  const [gut, setGut] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [schlecht, setSchlecht] = useState(0)

  const setGutHandler = () => setGut(gut+1)
  const setNeutralHandler = () => setNeutral(neutral+1)
  const setSchlechtHandler = () => setSchlecht(schlecht+1)

  return (
    <div>
      <h1>give feedback</h1>
      <div>
        <Button text='gut' onClick={setGutHandler} />
        <Button text='neutral' onClick={setNeutralHandler} />
        <Button text='schlecht' onClick={setSchlechtHandler} />
      </div>
      <div>
        <p>
          <Statistic gut={gut} neutral={neutral} schlecht={schlecht} />
        </p>
      </div>
    </div>
  );
}

export default App;
