const Header = (props) => (
  <h1>{props.course}</h1>
)

const Content = (props) => (
<p>
  {props.part} {props.exercisesAmount}
</p>
)

const Total = (props) => {
  return (
    <p>Anzahl von Aufgaben {props.sum}</p>
  )

}


const App = () => {
  const course = 'Half Stack application development'

  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10,
  }

  const part2 = {
    name: 'Using props to pass data',
    exercises: 21,
  }

  const part3 = {
    name: 'State of a component',
    exercises: 7
  }

  return (
    <div>
      <Header course="Half Stack application development" />
      <Content part={part1.name} exercisesAmount={part1.exercises} />
      <Content part={part2.name} exercisesAmount={part2.exercises} />
      <Content part={part3.name} exercisesAmount={part3.exercises} />
      <Total sum={part1.exercises + part2.exercises + part3.exercises} />
    </div>
  )
}

export default App
