const Header = (props) => (
  <h1>{props.course}</h1>
)

const Part = (props) => (
  <p>{props.part} {props.exercisesAmount}</p>
)

const Content = (props) => (
  <div>
  <Part part={props.course.parts[0].name} exercisesAmount={props.course.parts[0].exercises} />
  <Part part={props.course.parts[1].name} exercisesAmount={props.course.parts[1].exercises} />
  <Part part={props.course.parts[2].name} exercisesAmount={props.course.parts[2].exercises} />
  </div>
)

const Total = (props) => {
  return (
    <p>Anzahl von Aufgaben {props.sum}</p>
  )

}


const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
    {
      name: 'Fundamentals of React',
      exercises: 10,
    },
    {
      name: 'Using props to pass data',
      exercises: 21,
    },
    {
      name: 'State of a component',
      exercises: 9
    }
  ]
}


  return (
    <div>
      <Header course={course.name} />
      <Content course={course} />
      <Total sum={course.parts[0].exercises + course.parts[1].exercises + course.parts[2].exercises} />
    </div>
  )
}

export default App
