  <h1>{props.course}</h1>
)

const Content = (props) => (
<p>
  {props.part} {props.exercisesAmount}
</p>
)

const Total = (props) => {
  return (
    <p>Anzahl von Aufgaben {props.sum}</p>
  )

}


const App = () => {
  const course = 'Half Stack application development'

  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10,
    },
    {
      name: 'Using props to pass data',
      exercises: 21,
    },
    {
      name: 'State of a component',
      exercises: 7
    }
  ]


  return (
    <div>
      <Header course="Half Stack application development" />
      <Content part={parts[0]['name']} exercisesAmount={parts[0]['excercises']} />
      <Content part={parts[1]['name']} exercisesAmount={parts[1]['excercises']} />
      <Content part={parts[2]['name']} exercisesAmount={parts[2]['excercises']} />
      <Total sum={parts[0].exercises + parts[1].exercises + partss[2].exercises} />
    </div>
  )
}

export default App
